create table customer(
customerid number generated by default on null as identity primary key,
customername varchar2(100),
address varchar(200),
city varchar(100),
state varchar(100),
zip NUMBER
);

create table accounts(
accountid number generated by default on null as identity primary key,
customerid number,
accountnumber number,
dateopened varchar2(100),
dateclosed varchar2(100),
accounttype varchar2(100),
balance NUMBER(10,2),
foreign key (customerid) references customer(customerid)
);

drop table transactions;
drop table accounts;

create table transactions(
transactionid number generated by default as identity primary key,
accountid number,
transactiondate varchar2(100),
amount NUMBER(10,2),
transactiontype varchar(100),
foreign key (accountid) references accounts(accountid)
);

